import React from "react";
import PropTypes from "prop-types";
import Head from "next/head";
import { ThemeProvider } from "@material-ui/core/styles";
import theme from "../src/theme";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import MailIcon from "@material-ui/icons/Mail";
import Link from "../src/Link";
import { makeStyles } from "@material-ui/core/styles";
import { Button, Collapse, IconButton, MenuItem } from "@material-ui/core";
import { ExpandLess, ExpandMore } from "@material-ui/icons";
import MenuIcon from "@material-ui/icons/Menu";
import Switch from "@material-ui/core/Switch";
import Brightness7 from "@material-ui/icons/Brightness7";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
	root: {
		display: "flex",
		flexGrow: 1,
	},
	drawer: {
		width: drawerWidth,
		flexShrink: 0,
	},
	drawerPaper: {
		width: drawerWidth,
		backgroundColor: "black",
	},
	// necessary for content to be below app bar
	toolbar: theme.palette.background.default,
	content: {
		flexGrow: 1,
		backgroundColor: theme.palette.background.default,
		padding: theme.spacing(3),
	},
	nested: {
		paddingLeft: theme.spacing(4),
	},
	menuButton: {
		marginRight: theme.spacing(2),
	},
	title: {
		flexGrow: 1,
	},
	appBar: {
		zIndex: theme.zIndex.drawer + 1,
	},
}));

export default function MyApp(props) {
	const classes = useStyles();
	const [open, setOpen] = React.useState(true);
	const [selectedIndex, setSelectedIndex] = React.useState(1);
	const [state, setState] = React.useState(true);

	const handleChange = (event) => {
		setState(!state);
	};
	const { Component, pageProps } = props;

	const handleListItemClick = (event, index) => {
		setSelectedIndex(index);
	};

	const handleClick = () => {
		setOpen(!open);
	};

	React.useEffect(() => {
		// Remove the server-side injected CSS.
		const jssStyles = document.querySelector("#jss-server-side");
		if (jssStyles) {
			jssStyles.parentElement.removeChild(jssStyles);
		}
	}, []);

	return (
		<React.Fragment>
			<Head>
				<title>Minehut</title>
				<meta
					name="viewport"
					content="minimum-scale=1, initial-scale=1, width=device-width"
				/>
			</Head>
			<ThemeProvider theme={theme}>
				{/* CssBaseline kickstart an elegant, consistent, and simple baseline to build upon. */}
				<CssBaseline />
				<AppBar position="relative" className={classes.appBar}>
					<Toolbar>
						<Typography variant="h6" className={classes.title}>
							Minehut
						</Typography>
						<Button onClick={handleChange}>
							<Brightness7 />
						</Button>
					</Toolbar>
				</AppBar>
				<Drawer
					className={classes.drawer}
					variant="permanent"
					classes={{
						paper: classes.drawerPaper,
					}}
					anchor="left"
				>
					<div className={classes.toolbar} />
					<List dense>
						{["Inbox", "Starred", "Send email"].map(
							(text, index) => (
								<ListItem
									component={Link}
									naked
									href="about"
									button
									key={text}
									selected={selectedIndex === index}
									onClick={(event) =>
										handleListItemClick(event, index)
									}
								>
									<ListItemIcon>
										{index % 2 === 0 ? (
											<InboxIcon />
										) : (
											<MailIcon />
										)}
									</ListItemIcon>
									<ListItemText primary={text} />
								</ListItem>
							)
						)}
						<ListItem onClick={handleClick} button key="Drafts">
							<ListItemIcon>
								<InboxIcon />
							</ListItemIcon>
							<ListItemText primary="Drafts" />
							{open ? <ExpandLess /> : <ExpandMore />}
						</ListItem>
						<Collapse in={open} timeout="auto" unmountOnExit>
							<List dense component="div" disablePadding>
								<ListItem button className={classes.nested}>
									<ListItemIcon>
										<MailIcon />
									</ListItemIcon>
									<ListItemText primary="Draft 1" />
								</ListItem>
							</List>
						</Collapse>
					</List>
				</Drawer>
				<Component {...pageProps} />
			</ThemeProvider>
		</React.Fragment>
	);
}

MyApp.propTypes = {
	Component: PropTypes.elementType.isRequired,
	pageProps: PropTypes.object.isRequired,
};
