import React from "react";
import {
	Drawer,
	Toolbar,
	List,
	ListItem,
	ListItemIcon,
	ListItemText,
	Collapse,
	Link,
} from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import InboxIcon from "@material-ui/icons/Inbox";
import { makeStyles } from "@material-ui/core/styles";
import theme from "./theme";
import { ExpandLess, ExpandMore } from "@material-ui/icons";

const drawerWidth = 350;

const useStyles = makeStyles({
	drawer: {
		width: drawerWidth,
		flexShrink: 0,
		textAlign: "right",
	},
	drawerPaper: {
		width: drawerWidth,
		// backgroundColor: theme.palette.type === "dark" ? "#303030" : "#eeeeee",
		//background: "linear-gradient(120deg, #7289da, #66a6ff)",
		backgroundColor: "#2e3238",
	},
	drawerContainer: {
		overflowY: "auto",
		overflowX: "hidden",
		marginTop: theme.spacing(4),
	},
});

export default function CustomDrawer() {
	const classes = useStyles();

	const [open, setOpen] = React.useState(false);
	const [selectedIndex, setSelectedIndex] = React.useState(1);

	const handleListItemClick = (event, index) => {
		setSelectedIndex(index);
	};

	const handleClick = () => {
		setOpen(!open);
	};

	return (
		<Drawer
			className={classes.drawer}
			variant="permanent"
			classes={{
				paper: classes.drawerPaper,
			}}
			anchor="left"
		>
			<Toolbar />
			<div className={classes.drawerContainer}>
				<List dense>
					{["Inbox", "Starred", "Send email"].map((text, index) => (
						<ListItem
							component={Link}
							naked
							href="/"
							button
							key={text}
							selected={selectedIndex === index}
							onClick={(event) =>
								handleListItemClick(event, index)
							}
						>
							<ListItemIcon>
								{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
							</ListItemIcon>
							<ListItemText primary={text} />
						</ListItem>
					))}
					<ListItem onClick={handleClick} button key="Drafts">
						<ListItemIcon>
							<InboxIcon />
						</ListItemIcon>
						<ListItemText primary="Drafts" />
						{open ? <ExpandLess /> : <ExpandMore />}
					</ListItem>
					<Collapse in={open} timeout="auto" unmountOnExit>
						<List dense component="div" disablePadding>
							<ListItem
								button
								onClick={(event) =>
									handleListItemClick(event, 3)
								}
								selected={selectedIndex === 3}
							>
								<ListItemIcon className={classes.nestedText}>
									<MailIcon />
								</ListItemIcon>
								<ListItemText primary="Draft 1" />
							</ListItem>
						</List>
					</Collapse>
				</List>
			</div>
		</Drawer>
	);
}
